        methods:{
            
            dropdown(index) {
                this.menu = index;
            },
            dropdownoff(){
                this.menu = false
                
            },
            selected(index){
                this.active = index;
            },
            sendNew(){
                var now = new Date();
                var current = now.getHours() + ':' + now.getMinutes();
                this.online = true
                this.writing = true
                if ((this.newMsg !== '') && (this.newMsg.trim().length !== 0)) {
                    this.contacts[this.active].messages.push(
                        {
                        message: this.newMsg,
                        status: 'sent',
                        date : current
                        },                  
                    )
                } else {
                    return this.newMsg = '';
                }
                this.newMsg="";
                setTimeout(() =>{                
                    var x = Math.floor(Math.random() * 7);
                    const msgFromCp = ["Ciao","come stai?","Che mi racconti?","Puoi parlare?","Non mi scrivere piÃ¹", "Non voglio parlare", "ahahahahahah", "cosa vuoi?"];

                    this.contacts[this.active].messages.push(
                        
                        {
                            message : msgFromCp[x],
                            status : 'received',
                            date : current
                        }
                        
                    )
                    this.writing = false
                    this.online = true
                }, 3000)
               
                
                setTimeout(() =>{                
                
                    this.prova = false
                    this.online = false
                    
                }, 5000)
                
            },
            deleteMsg(indice){
                {
                    this.contacts[this.active].messages.splice(indice,1)
                  } 
            },
            searchContact(){
                for(let i = 0; i < this.contacts.length; i++){
                    if (this.contacts[i].name.toUpperCase().includes(this.searchName.toUpperCase())) {
                        this.contacts[i].visible = true;
                    }
                    else {
                        this.contacts[i].visible = false;
                    }
                }
            },  
            openEmoticon(){
                if (this.menuEmoji === false){
                    this.menuEmoji = true
                    console.log(this.menuEmoji);
                }
                else{
                    this.menuEmoji = false
                    console.log(this.menuEmoji);
                }
            }
            
        }

